class VistaICM2_Event : public Event_Impl
{
public:

	/**
	* @brief Constructors
	*/
	VistaICM2_Event(int DeviceID, string ServerAddress, bool bConnectEventHandler=true) :
		Event_Impl(DeviceID, DEVICETEMPLATE_VistaICM2_CONST, ServerAddress, bConnectEventHandler, SOCKET_TIMEOUT) {};
	VistaICM2_Event(class ClientSocket *pOCClientSocket, int DeviceID) : Event_Impl(pOCClientSocket, DeviceID) {};

	/**
	* @brief Events builder method
	*/
	class Event_Impl *CreateEvent(unsigned long dwPK_DeviceTemplate, ClientSocket *pOCClientSocket, unsigned long dwDevice);

	/**
	* @brief Events methods for our device
	*/

	virtual void Sensor_Tripped(bool bTripped)
	{
		SendMessage(new Message(m_dwPK_Device, DEVICEID_EVENTMANAGER, PRIORITY_NORMAL, MESSAGETYPE_EVENT, 
			EVENT_Sensor_Tripped_CONST,
			1 /* number of parameter's pairs (id, value) */,
			EVENTPARAMETER_Tripped_CONST, (bTripped ? "1" : "0")));
	}

	virtual void Fire_Alarm(int iPK_Device)
	{
		SendMessage(new Message(m_dwPK_Device, DEVICEID_EVENTMANAGER, PRIORITY_NORMAL, MESSAGETYPE_EVENT, 
			EVENT_Fire_Alarm_CONST,
			1 /* number of parameter's pairs (id, value) */,
			EVENTPARAMETER_PK_Device_CONST, StringUtils::itos(iPK_Device).c_str()));
	}

	virtual void Reporting_Child_Devices(string sError_Message,string sText)
	{
		SendMessage(new Message(m_dwPK_Device, DEVICEID_EVENTMANAGER, PRIORITY_NORMAL, MESSAGETYPE_EVENT, 
			EVENT_Reporting_Child_Devices_CONST,
			2 /* number of parameter's pairs (id, value) */,
			EVENTPARAMETER_Error_Message_CONST, sError_Message.c_str(),
			EVENTPARAMETER_Text_CONST, sText.c_str()));
	}

	virtual void Alarm_panel_mode_change(string sValue,int iZoneNo)
	{
		SendMessage(new Message(m_dwPK_Device, DEVICEID_EVENTMANAGER, PRIORITY_NORMAL, MESSAGETYPE_EVENT, 
			EVENT_Alarm_panel_mode_change_CONST,
			2 /* number of parameter's pairs (id, value) */,
			EVENTPARAMETER_Value_CONST, sValue.c_str(),
			EVENTPARAMETER_ZoneNo_CONST, StringUtils::itos(iZoneNo).c_str()));
	}

};


